mapper.select:
SELECT u.id id, u.userType, s.studentNumber studentNumber, s.name studentName, s.grade grade, d.departmentName departmentName FROM User u LEFT JOIN Student s ON u.id=s.userId LEFT JOIN Department d ON s.departmentId=d.id WHERE INSTR(s.studentNumber, #{st})>0 WHERE INSTR(s.name, #{st})>0 WHERE INSTR(d.departmentName, #{st})>0 WHERE INSTR(u.userType, #{st})>0 ORDER BY
studentNumber studentNumber DESC studentName studentName DESC grade grade DESC departmentName departmentName DESC id
LIMIT ${start}, ${sz}

mapper.update:
UPDATE User
SET userType=#{userType}
WHERE id=#{id}
